import React, { Component } from "react";
import axios from "axios";
import HomeSale from "./components/HomeSale";
import Loader from "../../components/Loader/Index";
import "./Home.css";

class Home extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      product: []
    };
  }

  componentDidMount() {

      axios.get(`http://localhost:8081/products`)
        .then(res => {
          const product = res.data;
          this.setState({
            loading: false,
            product: [ {
              "id": 1,
              "name": "men's analog quartz watch",
              "slug": "mens-analog-quartz-watch-547383",
              "price": 500,
              "discount_price": 2800,
              "category": "men",
              "color": "black",
              "subcategory": "",
              "sale": true,
              "article": "watch",
              "quantity": 5,
              "img": "analog-quartz-watch.jpg",
              "options": [
                  1,
                  2,
                  3
              ],
              "fulfilled_by_duka": true,
              "shipped_from_abroad": false,
              "duka_approved": true,
              "vendor": {
                  "id": 1,
                  "name": "anglebroking"
              },
              "ratings": {
                  "star_ratings": 4.8,
                  "votes": 350
              }
          },]
          });
        }).then(err => {
          this.setState({
            error: err,
            loading: false,
            product: []
          })
        }).finally(() => {
          this.setState({
            loading: false,
            product: []
          })
        })
  }

  render() {
    return this.state.loading ? (
      <Loader />
    ) : (
        <React.Fragment>
          <HomeSale product={this.state.product} />
        </React.Fragment>
      );
  }
}

export default Home;
