import React, { Component } from "react";
import HomeSale from "./components/HomeSale";
import Loader from "../../components/Loader/Index";
import "./Home.css";

class Home extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:8081/products`)
    .then(res => {
      const persons = res.data;
      this.setState({ persons });
    })
    this.setState({
      loading: false,
       product: [{
        "id": 8,
        "name": "Boho printed floral dress",
        "slug": "boho-printed-floral-dress-656623",
        "price": 1999,
        "discount_price": 2199,
        "category": "women",
        "color": "skyblue",
        "sizes": [
          "M",
          "L",
          "XL"
        ],
        "subcategory": "",
        "sale": true,
        "article": "dress",
        "quantity": 10,
        "img": "floral-dress.jpg",
        "options": [],
        "fulfilled_by_duka": false,
        "shipped_from_abroad": false,
        "duka_approved": false,
        "vendor": {
          "id": 3,
          "name": "vendor three"
        },
        "ratings": {
          "star_ratings": 3.6,
          "votes": 129
        }
      }]
    });
  }
  render() {
    return this.state.loading ? (
      <Loader />
    ) : (
        <React.Fragment>
          <HomeSale product={this.state.product} />
        </React.Fragment>
      );
  }
}

export default Home;
